/* soap-bowtie-easy.js */

/* code generated by command: python3 create_level.py "name=soap-bowtie-easy" "initialSpells=remove-letter,change-letter" "targetThing=treasure" "room=name:room1,things:bowtie soap hater,exits:E/room2/hater N/room0" "room=name:room0,things:jammer,exits:S/room1" "room=name:room2,things:partition,exits:E/room3/partition W/room1" "room=name:room3,things:lava,exits:E/room4/lava W/room2" "room=name:room4,things:treasure,exits:W/room3" */

levelList.push( { name:'soap-bowtie-easy', difficulty:0 } );

getLevelFunctions['soap-bowtie-easy'] = function() {

    let level = new Level('soap-bowtie-easy');
    level.levelPath = 'soap-bowtie-easy';

    level.defineThingSubclasses = function() {

        window.Hammer = class Hammer extends Thing {
            handleDblclick(e) {
                if (!(this.word in inventory) || !(('partition' in thingsHere) || ('ravelin' in thingsHere)))
                    return super.handleDblclick(e);
                this.strokeNumber = 0;
                this.discard();
                let target = ('partition' in thingsHere) ? thingsHere['partition'] : thingsHere['ravelin'];
                this.x =  target.x - 25;
                this.y = target.y - 25;
                this.startStroke();
                if (typeof this.captionDiv !== 'undefined') {
                    this.captionDiv.style.display = 'none'; // don't display caption while saw being used
                }
            }

            /* TODO: suppress player input during motion */

            startStroke() {
                console.log('sS');
                this.strokeNumber++;
                let destX = this.x ; // - 70;
                let destY = this.y ; // + 4;
                let time = 110;
                if (this.strokeNumber % 2 == 1) {
                    destX = this.x ; // + 70;
                    destY = this.y ; // + 13;
                    time = 600;
                }
                this.setMovement(destX, destY, time);
                if (this.strokeNumber < 8) {
                    this.methodToCallAfterMovement = this.startStroke.bind(this);
                }
                else if ('partition' in thingsHere) {
                    this.methodToCallAfterMovement = this.removePartition.bind(this);
                }
                else { // trying to remove ravelin but too strong.
                    this.methodToCallAfterMovement = this.failToRemoveRavelin.bind(this);
                }
            }

            draw() {
                let rotation = 0;
                if (this.beginMovementTime > 0) {
                    rotation = (((Date.now() - this.beginMovementTime) / this.movementDurationMS) * Math.PI / 2 ) - (Math.PI/2);
                    if (this.strokeNumber % 2 == 1)
                        rotation = - rotation - (Math.PI / 2);
                }
                // console.log(rotation);
                if (this.beginMovementTime > 0) {
                    /*   ctx.save();
                    ctx.translate(this.x, this.y);
                    ctx.rotate(rotation);
                    */
                    ctx.setTransform(1, 0, 0, 1, this.x + 10, this.y - 50);
                    ctx.rotate(rotation);
                    ctx.drawImage(this.image, 60 - this.halfWidth, 60 - this.halfHeight, this.width, this.height);
// ctx.drawImage(this.image, 0 - (this.x - this.halfWidth), 0 - (this.y - this.halfHeight), this.width, this.height);
//
                    // ctx.drawImage(this.image, 0 - (this.x - this.halfWidth), 0 - (this.y - this.halfHeight), this.width, this.height);
                    ctx.rotate(0-rotation);
                    ctx.setTransform(1,0,0,1,0,0);
                }
                else
                    ctx.drawImage(this.image, this.x - this.halfWidth, this.y - this.halfHeight, this.width, this.height);
                /* if (rotation > 0)
                    ctx.restore(); */
            }

            stopStriking() {
                if (typeof this.captionDiv !== 'undefined') {
                    this.captionDiv.style.display = 'block';
                }
                this.beginMovementTime = 0;
                this.movementDurationMS = 0;
                this.tryToPickUp();
            }

            removePartition() {
                this.stopStriking();
                thingsHere['partition'].dispose();
            }

            failToRemoveRavelin() {
                this.stopStriking();
                displayMessage('The ravelin is too strong!');
            }
        }

        window.Hater = class Hater extends Thing {
        }

        window.Java = class Java extends Thing {
        }

        window.Lava = class Lava extends Thing {
        }

        window.Partition = class Partition extends Thing {
        }

        window.Treasure = class Treasure extends Thing {
        }

        window.Water = class Water extends Thing {
        }

    }

    level.getThing = function(word,room,x,y) {
        switch (word) {
            case 'hammer' : return new Hammer(word,room,x,y);
            case 'hater' : return new Hater(word,room,x,y);
            case 'java' : return new Java(word,room,x,y);
            case 'lava' : return new Lava(word,room,x,y);
            case 'partition' : return new Partition(word,room,x,y);
            case 'treasure' : return new Treasure(word,room,x,y);
            case 'water' : return new Water(word,room,x,y);
            default : return undefined; // this will cause instantiation of plain-vanilla Thing.
        }
    }
    level.initialRoom = 'room1';
    level.initialX = 50; // expressed as % of way across x axis, i.e. value range is 0-100
    level.initialY = 55;
    level.initialSpells = [ 'remove-letter', 'change-letter' ];
    level.initialInventory = {};
    level.backgroundMusicFile = undefined;
    level.allWords = [ 'bootie','bowtie','hammer','hater','jammer','java','lap','lav','lava','lavs','law','partition','paw','sap','saw','soap','sop','treasure','water' ];
    level.initialThings = [ ['bowtie','room1',40,65],['soap','room1',60,65],['hater','room1',81,50],['jammer','room0',40,65],['partition','room2',75,50],['lava','room3',81,50],['treasure','room4',40,65] ];
    level.immovableObjects = [ 'partition','jammer','hater','water','lava' ];

    level.targetThing = 'treasure';
    level.initialRunes = [];

    level.rooms = {
        'room1': {
            boundaries: [],
            filledPolygons: [],
            passages: [
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'E',97, 50, 'room2', 10, 50, true, 65, 50, 'hater', PASSAGE_STATE_BLOCKED, 73, 50),
                new Passage(PassageTypes.INVISIBLE_VERTICAL, 'N',50, 3, 'room0', 50, 90, true, 50, 50)],
        },
        'room0': {
            boundaries: [],
            filledPolygons: [],
            passages: [
                new Passage(PassageTypes.INVISIBLE_VERTICAL, 'S',50, 97, 'room1', 50, 10, true, 50, 50)],
        },
        'room2': {
            boundaries: [],
            filledPolygons: [],
            passages: [
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'E',97, 50, 'room3', 10, 50, true, 65, 50, 'partition', PASSAGE_STATE_BLOCKED, 73, 50),
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'W',3, 50, 'room1', 90, 50, true, 50, 50)],
        },
        'room3': {
            boundaries: [],
            filledPolygons: [],
            passages: [
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'E',97, 50, 'room4', 10, 50, true, 25, 50, 'lava', PASSAGE_STATE_BLOCKED, 73, 50),
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'W',3, 50, 'room2', 90, 50, true, 50, 50)],
        },
        'room4': {
            boundaries: [],
            filledPolygons: [],
            passages: [
                new Passage(PassageTypes.INVISIBLE_HORIZONTAL, 'W',3, 50, 'room3', 90, 50, true, 50, 50)],
        },
    };
    return level;
}